function pageprocessorMerchantApp(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.selectedTab = true;
  $scope.nameList = [];
  $scope.orderHistory = [];
  $scope.showNear = true;
  $scope.showOrderHistory = false;
  $scope.hideMyOrderDialog = true;
  $scope.hideOrderHistoryDialog = true;
  var testUrl = "https://kryptosda.kryptosmobile.com/kryptosds/bigdeal/getMerchantOrdersTest";
  var prodUrl = "https://kryptosda.kryptosmobile.com/kryptosds/bigdeal/getMerchantOrders";
  var pickUpImage = "https://kryptosda.kryptosmobile.com/kryptosds/imagegallery/6f2f0436-12fc-4f7d-909c-a7dec57e76a0.jpg";
  var deliveryImage = "https://kryptosda.kryptosmobile.com/kryptosds/imagegallery/29462faf-fbfe-47cf-ad41-441ab2a724bd.jpg";
  var merchantData = $.jStorage.get('merchantInfo');
  if (!merchantData) {
    $location.path("app/MerchantApp/MerchantAppPage3");
  } else {
    $.blockUI({
      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0, so always add + 1
    var yyyy = today.getFullYear();
    if (dd < 10) {
      dd = '0' + dd
    }
    if (mm < 10) {
      mm = '0' + mm
    }
    today = yyyy + '-' + mm + '-' + dd;

    var url = testUrl;
    var proxyMethod = "POST";
    var proxyData = {
      "tenant": "BigDeal",
      "restId": merchantData.restId,
      "date": today
    };
    callApi(proxyData, function(data) {

      if (data.success == false) {
        console.log("no result");
        $scope.hideMyOrderDialog = false;
      } else {
        if (!data.success && data.length != 0) {
          $scope.nameList = data.reverse();
        }
      }
      var isSubscribe = $.jStorage.get('subscribe');
      if (!isSubscribe)
        subscribeMerchant();

    }, function(err) {

    })

  }
  $scope.myOrderClick = function() {
    if ($scope.nameList.length == 0)
      $scope.hideMyOrderDialog = false;
    $scope.foodDelivery = false;
    $scope.selectedTab = true;
    $scope.hideOrderHistoryDialog = true;
    $("#dineinTab").addClass('foodTabSelected').removeClass('foodTabUnSelected');
    $("#foodDeliveryTab").addClass('foodTabUnSelected').removeClass('foodTabSelected');
  };

  $scope.orderHistoryClick = function() {

    if ($scope.orderHistory.length == 0) {
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });

      var proxyData = {
        "tenant": "BigDeal",
        "restId": merchantData.restId
      };
      callApi(proxyData, function(data) {

        if (data.success == false) {
          console.log("no result");
          $scope.hideOrderHistoryDialog = false;
        } else {
          if (!data.success && data.length != 0) {
            $scope.orderHistory = data.reverse();
          }
        }

      }, function(err) {

      })

    }
    $scope.hideMyOrderDialog = true;
    $scope.foodDelivery = true;
    $scope.selectedTab = false;
    $("#foodDeliveryTab").addClass('foodTabSelected').removeClass('foodTabUnSelected');
    $("#dineinTab").addClass('foodTabUnSelected').removeClass('foodTabSelected');
  };

  $scope.openDeal = function(order) {
    $rootScope.orderDetail = order;
    $location.path("app/MerchantApp/MerchantAppPage2");
  };

  function callApi(proxyData, successCallback, errorCallback) {
    try {
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        console.log(JSON.stringify(data));
        successCallback(data);
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
        errorCallback(data);
      });

    } catch (e) {}

  }

  function subscribeMerchant() {

    try {
      parsePlugin.subscribe(merchantData.restId, function() {
        $.unblockUI();
        $.jStorage.set('subscribe', true);
      }, function(e) {
        $.unblockUI();
        $.jStorage.set('subscribe', undefined);
      });

    } catch (e) {}

  }
}

function pageprocessorMerchantAppPage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.orderDetail = $rootScope.orderDetail;
    console.log(JSON.stringify($scope.orderDetail));
    $scope.addDeliverycharge = false;
    $scope.custDetail = true;
    $scope.orderStatus="";
    $scope.subtotal = $scope.orderDetail.order.subtotal;
    $scope.discount = $scope.orderDetail.order.discount;
    $scope.discountamount = $scope.orderDetail.order.discountamount;
    $scope.serviceTaxAmount = $scope.orderDetail.order.serviceTaxAmount;
    $scope.serviceChargeAmount = $scope.orderDetail.order.serviceChargeAmount;
    $scope.vatAmount = $scope.orderDetail.order.vatAmount;

    if ($scope.orderDetail.order.orderType == 'Delivery') {
        $scope.addDeliverycharge = true;
        $scope.deliveryChargeAmount = $scope.orderDetail.order.deliveryChargeAmount;

    } else {
        $scope.addDeliverycharge = false;
    }
    $scope.payableamount = $scope.orderDetail.order.payableamount;
    $scope.name = $scope.orderDetail.name;
    $scope.showDetails = function () {
        $scope.custDetail = !$scope.custDetail;
        $scope.$apply();
    }
    if ($scope.orderDetail.status=='pending'){
        $scope.orderStatus="Pending";
        $("#status").addClass('pending');
    }else if ($scope.orderDetail.status=='confirmed'){
        $scope.orderStatus="Confirmed";
        $("#status").addClass('confirmed');
    }else if ($scope.orderDetail.status=='declined'){
        $scope.orderStatus="Declied";
        $("#status").addClass('pending');
    }
}

function pageprocessorMerchantAppPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.username = "";
    $scope.password = "";
    var testUrl = "https://kryptosda.kryptosmobile.com/kryptosds/bigdeal/merchantLoginTest";
    var prodUrl = "https://kryptosda.kryptosmobile.com/kryptosds/bigdeal/merchantLogin"
    $scope.login = function () {

        console.log($scope.username + "username\n" + $scope.password);

        if ($scope.username == "" || $scope.username == undefined) {
            if (window.device) {
                navigator.notification.alert(
                    "Please enter username.",  // message
                    null,              // callback to invoke with index of button pressed
                    '',            // title
                    'Ok'          // buttonLabels
                );

            } else {
                alert("Please enter username.");
            }
            return;

        }
        if ($scope.password == "" || $scope.password == undefined) {
            if (window.device) {
                navigator.notification.alert(
                    "Please enter password",  // message
                    null,              // callback to invoke with index of button pressed
                    '',            // title
                    'Ok'          // buttonLabels
                );

            } else {
                alert("Please enter password.");
            }
            return;

        }
        else if (!$scope.username == "" && !$scope.password == "") {
            $.blockUI({
                message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
            });
            var url = testUrl;
            var proxyMethod = "POST";
            var proxyData = {
                "tenant": "BigDeal",
                "username": $scope.username,
                "password": $scope.password
            };
            try {
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function (data, status, headers, config) {
                    $.unblockUI();
                    console.log(JSON.stringify(data));
                    if (data.success == true) {
                        var merchantData = data.merchantDetails[0];
                        alert(JSON.stringify(merchantData));
                        $.jStorage.set('merchantInfo', merchantData)
                        try {
                            var isSubscribe = $.jStorage.get('subscribe');
                            if (!isSubscribe) {
                                alert("Subscribing user");
                                parsePlugin.subscribe(merchantData.restId, function () {
                                    alert('Subscribe');
                                    $.unblockUI();
                                    $.jStorage.set('subscribe', true);
                                    $location.path("app/MerchantApp/MerchantApp");
                                    $scope.$apply();
                                }, function (e) {
                                    alert('error');
                                    $.unblockUI();
                                    $.jStorage.set('subscribe', undefined);
                                    $location.path("app/MerchantApp/MerchantApp");
                                    $scope.$apply();
                                });

                            }else {
                                $location.path("app/MerchantApp/MerchantApp");
                                $scope.$apply();

                            }
                           
                        } catch (e) {
                            alert(e);
                        }

                        $scope.$apply();
                    } else {
                        if (window.device) {
                            navigator.notification.alert(
                                "In-correct username and password.",  // message
                                null,              // callback to invoke with index of button pressed
                                '',            // title
                                'Ok'          // buttonLabels
                            );

                        } else {
                            alert("In-correct username and password.");
                        }

                    }

                }).
                error(function (data, status, headers, config) {
                    $.unblockUI();


                });

            }
            catch (e) {

            }

        }


    }
}
